svg.script:
const svg = document.getElementById('testSvg');
const circle = document.getElementById('testCircle');
const rect = document.getElementById('testRect');

svg.addEventListener('mouseover', () => {
  console.log('Mouse over the SVG');
});

svg.addEventListener('mouseout', () => {
  console.log('Mouse out of the SVG');
});

circle.addEventListener('mouseover', () => {
  console.log('Mouse over the circle');
});

circle.addEventListener('mouseout', () => {
  console.log('Mouse out of the circle');
});

circle.addEventListener('click', () => {
  console.log('Circle clicked');
});

rect.addEventListener('mouseover', () => {
  console.log('Mouse over the rectangle');
});

rect.addEventListener('mouseout', () => {
  console.log('Mouse out of the rectangle');
});

rect.addEventListener('click', () => {
  console.log('Rectangle clicked');
});
